power_user.custom_css:
/*======================================================
   样式作者：@Junezz
   版本：v1.55
   发布于：类脑 Discord 社区
   本样式仅供个人使用与参考，禁止任何形式的商用。
   可二改不可二传。请保留本注释和原作者署名。
======================================================*/

/* 可自改部分已标注 */
/* 字体 */
@import url("https://fontsapi.zeoseven.com/301/main/result.css");

body {
    font-family: "GenWanMin2 TC R";
    font-weight: normal;
}

/* 头像 */
:root {
    --avatar-zoom: 1.25;
/* 头像放大比例：1=原比例，1.5=放大50%，如此类推 */
    --avatar-pan-x: -10%;
/* 头像水平平移：正数调左，负数调右。0%=居中 */
    --avatar-pan-y: 10%;
/* 头像垂直平移：正数调上，负数调下。0%=居中 */
/* 请从小的数值开始调，比如10%，接着+/-5%或10%递进，慢慢试着调，一上来就拉到100%的宝子不要问我为什么对面人不见了(*'へ'*) */
}

/* 思维链自定义 */
.mes_reasoning_header_title {
  font-size: 0;
  letter-spacing: normal;
}

.mes_reasoning_header_title::before {
  content: "叮！你的外卖已送达(*˘︶˘*).。.:*♡"; /* 在引号里输入文字 */
  font-size: 15px; /* 自调字体大小*/
  letter-spacing: 0.5px;
}

/* 删除顶栏阴影 */
#top-bar {
  box-shadow: none !important;
}

/* 隐藏QR */
#send_form:hover #qr--bar {    
    height: 30px;    
    transition: all 0.5s ease;
}

#qr--bar {    
    height: 0.1px;
}

/* 移动端滚动条隐藏 */
@media screen and (max-width: 1000px) {
    *::-webkit-scrollbar {
        display: none;
    }

    * {
        scrollbar-width: none;
        -ms-overflow-style: none;
    }
}
/* 可自改部分结束*/

code, #send_textarea, input, select, button, .text_pole {
  font-family: inherit;
}

  :root {
    --mainFontSize: calc(var(--fontScale)* 16px); /* 主字体大小 */
    --avatar-base-width: 48px; /* 头像宽度 */
    --avatar-base-height: 48px; /* 头像高度 */
    --genericRadius: 8px; /* 通用圆角 */
    --warning: #ce3636;
    --crimson70a: #9fc1d663;
    --crimson-hover: color-mix(in srgb, var(--SmartThemeUnderlineColor), transparent 80%) !important;
    --standardIconSize: calc(var(--mainFontSize)* 1);
    --bottomFormIconSize: calc(var(--mainFontSize)* 1.2);
    --bottomFormBlockPadding: calc(var(--mainFontSize) / 1.3);
    --scrollBarWidth: 5px;
    --pagePadding: calc(var(--scrollBarWidth) + 3px);
    --boxShadowSubtle: 0 2px 6px rgba(0, 0, 0, 0.16); 

    /* ==== 所有颜色定义 ==== */
    --utilityColor: var(--white50a); /* 说明文字 */
    --navBackgroundColor: color-mix(in srgb, var(--SmartThemeUnderlineColor), black 50%); /* 顶栏和底栏背景颜色 */
    --buttonFill: black; /* 按钮背景颜色 */
    --menuBackgroundColor: var(--navBackgroundColor); /* 菜单背景颜色 */
    --accentColor: var(--SmartThemeBorderColor); /* 装饰色1 思维链，文字装饰，段落装饰，头像滑轮条等 */
    --accentColorOverlay: color-mix(in srgb, var(--SmartThemeBorderColor), transparent 76%); /* 装饰色2半透明 消息里头像和名字的背景颜色 */
    --scrollBarColor: var(--SmartThemeUnderlineColor); /* 滚动条颜色 */
  }

  /* 聊天框背景 */
  #chat {
    padding: 0px 10px 0px 10px;
    overflow-y: scroll;
    border: none;
    background-color: transparent;
  }

  #top-bar {
    background-color: transparent !important;
    height: var(--topBarBlockSize);
  }

  #extensions_settings .inline-drawer-toggle.inline-drawer-header,
  #extensions_settings2 .inline-drawer-toggle.inline-drawer-header,
  #user-settings-block h4,
  .standoutHeader,
  .userSettingsInnerExpandable, .bg_example {
    border: transparent !important;
  }

  #left-nav-panel,
  #rm_api_block,
  #AdvancedFormatting,
  #WorldInfo,
  #user-settings-block,
  #Backgrounds,
  #rm_extensions_block,
  .drawer-content.openDrawer,
  #right-nav-panel {
    background-color: var(--menuBackgroundColor) !important;
    border-radius: 0px 0px var(--genericRadius) var(--genericRadius);
    padding: 8px;
    border: none;
  }

  .drawer-content {
    border-top-right-radius: 0px;
    border-top-left-radius: 0px;
    box-shadow: var(--boxShadowSubtle);
    padding-top: 8px;
  }

  /* Icon Sizes & Colors */
  #completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt span span span,
  #completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt .completion_prompt_manager_prompt_name .fa-solid,
  #rm_button_characters, #rm_button_panel_pin_div, #lm_button_panel_pin_div, #WI_button_panel_pin_div,
  #completion_prompt_manager .completion_prompt_manager_footer a,
  .fa, .fa-brands, .fa-classic, .fa-regular, .fa-sharp, .fa-solid, .fab, .far, .fas {
    font-size: var(--standardIconSize);
    color: var(--SmartThemeChatTintColor);
  }

  /* 预设icons */
  #completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt .prompt_manager_prompt_controls span {
    color: var(--SmartThemeChatTintColor) !important;
  }

  /* 对话框icons */
  .mes_button {
    color: var(--SmartThemeChatTintColor);
  }

  /* 底栏icon */
  #rightSendForm>div, #leftSendForm>div {
    font-size: var(--bottomFormIconSize);
    background-color: transparent !important;
    color: var(--SmartThemeChatTintColor);
  }

  /* 底栏icon 对齐 */
  #leftSendForm, #rightSendForm {
    align-items: center;
  }

  /* 标题样式 */

  /* 清除设置页相关模块标题的背景图，并精简上下 padding */
  #extensions_settings .inline-drawer-toggle.inline-drawer-header,
  #extensions_settings2 .inline-drawer-toggle.inline-drawer-header,
  #user-settings-block h4, .standoutHeader{
    background-image: none !important;
    padding: 2px 0 !important;
  }

 /* 原始元素样式 */
  .standoutHeader:not(h4),
  #rm_PinAndTabs,
  #title_api,
  #rm_api_block .flex-container.alignItemsBaseline,
  #AdvancedFormatting .flex-container.alignItemsBaseline:not(h3),
  #WorldInfo .alignitemscenter:not(#world_popup *),
  #user-settings-block div[name="userSettingsRowOne"],
  #rm_extensions_block .alignitemscenter.flex-container.wide100p:not(.justifyCenter),
  #top-settings-holder .drawer:not(:first-child):not(:last-child) .drawer-content .alignItemsBaseline.wide100p {
    position: relative; /* 必须要有 */
    padding-left: 12px;
    margin-top: calc(var(--mainFontSize) * 0.2) !important;
    margin-bottom: calc(var(--mainFontSize) * 0.2) !important;
  }

  /* 小竖线 */
  #rm_PinAndTabs::before,
  #title_api::before,
  #rm_api_block .flex-container.alignItemsBaseline::before,
  #AdvancedFormatting .flex-container.alignItemsBaseline:not(h3)::before,
  #WorldInfo .alignitemscenter:not(#world_popup *)::before,
  #user-settings-block div[name="userSettingsRowOne"]::before,
  #rm_extensions_block .alignitemscenter.flex-container.wide100p:not(.justifyCenter)::before,
  #top-settings-holder .drawer:not(:first-child):not(:last-child) .drawer-content .alignItemsBaseline.wide100p::before {
    content: '';
    position: absolute;
    left: 0;
    top: 6px;
    bottom: 6px;
    width: 3px;
    background-color: var(--SmartThemeUnderlineColor);
    border-radius: 1px;
  }

  /* === 统一标题文字的行高（使阅读更舒适）=== */
  .standoutHeader strong,
  #right-nav-panel h2,
  .drawer-content > h3,
  .drawer-content .flex-container h3:first-of-type:not(#Backgrounds .textAlignCenter),
  .drawer-content .flex-container .flex-container h3:first-of-type,
  .popup h3:not(dialog *) {
    line-height: 1.6;
  }

  .flex-container {
    gap: 8px;
  }

  .redOverlayGlow {
    color: #9c2c2c;
  }

  /* AI聊天气泡样式 */
  body.bubblechat .mes[is_user="false"]{
    margin-top: 290px;
    margin-bottom: 290px;
  }

  body.bubblechat .mes[is_user="true"]{
    background-color: var(--SmartThemeBotMesBlurTintColor);
  }

  /* menu颜色 */
  .ui-widget.ui-widget-content,
  #bulk_tag_popup,
  #dialogue_popup,
  .popup,
  #options,
  #extensionsMenu,
  .popup .popper-modal,
  .select2-dropdown,
  #select_chat_popup,
  #right-nav-panel,
  #left-nav-panel,
  #floatingPrompt,
  #cfgConfig,
  #logprobsViewer,
  #movingDivs > div {
    background-color: var(--menuBackgroundColor);
    box-shadow: var(--boxShadowSubtle);
    border: none;
  }

  .extensions_toolbar {
    background-color: var(--menuBackgroundColor);
  }

  .options-content, .list-group, .extensions_info .extension_block, #completion_prompt_manager_popup {
    border: none;
  }

  /* 背景添加页面 */
  .bg_example.no-border.no-shadow.add_bg_but.interactable {
    background-color: var(--SmartThemeUnderlineColor);
  } 

  /* 其他阴影 */
  .bg_example, #toast-container>div,
  .bg_example:hover, #toast-container>div:hover {
    box-shadow: var(--boxShadowSubtle);
  }

   /* 角色卡添加tag */
  body .ui-widget-content li:hover {
    background-color: var(--SmartThemeUnderlineColor);
  }

  .tag {
    background-color: var(--buttonFill);
  }

  /* 角色卡删除键 */
  .red_button:hover {
    background-color: var(--crimson-hover) !important;
  }

   /* 世界书 */
  .select2-container .select2-selection--multiple, .select2-container .select2-selection--single,
  .select2-results__option::before,
  .select2-results__option--selected.select2-results__option::before {
    background-color: var(--buttonFill);
  }

  .select2-container .select2-selection--multiple, .select2-container .select2-selection--single,
  .select2-container .select2-selection--multiple .select2-selection__choice, .select2-container .select2-selection--single .select2-selection__choice,
  .select2-container--default .select2-selection--multiple .select2-selection__choice,
  .standoutHeader~.inline-drawer-content,
  .regex-script-label {
    border: none !important;
  }

  .select2_choice_clickable_buttonstyle+span.select2-container .select2-selection__choice__display,
  span.select2.select2-container .select2-selection__choice__remove,
  .select2-container .select2-selection--multiple .select2-selection__choice, .select2-container .select2-selection--single .select2-selection__choice {
    background-color: var(--accentColorOverlay);
  }

  .select2_multi_sameline+span.select2-container .select2-selection--multiple .select2-search__field {
    background-color: transparent !important;
  }

  .standoutHeader~.inline-drawer-content {
    background-color: var(--menuBackgroundColor);
  }

  .wi-card-entry {
    margin-top: 12px;
    padding: 8px;
    background-color: var(--SmartThemeBorderColor);
    opacity: 1 !important;
    border: none;
  }

  .paginationjs-pages ul li a, .select_chat_block {
    border: none;
  }

  /* 聊天气泡 */
  body.bubblechat .mes {
    display: flex;
    flex-direction: column;
    align-items: center;
    background-color: var(--SmartThemeBotMesBlurTintColor);
    border-radius: 0px !important;
    border: 5px solid black;
    outline: 2px solid black;
    outline-offset: -10px;
    box-shadow: inset 0 0 0 3px color-mix(in srgb, black 30%, transparent 35%);
    position: relative; /* 为伪元素定位 */
    box-sizing: border-box; /* 确保padding和border包含在总宽度内 */
    background-clip: padding-box; /* 让背景不延伸到边框下，适配折角效果 */
}

/* 折角效果 */
body.bubblechat .mes:before,
body.bubblechat .mes:after {
  content: "";
  display: block;
  position: absolute;
  border: 30px solid transparent; /* 调整这个值可以改变折角大小 */
  transform: rotate(135deg);
}

/* 左上角的折角 */
body.bubblechat .mes:before {
  border-top: 30px solid color-mix(in srgb, var(--SmartThemeBodyColor), black 80%);
  opacity: 1 !important;
  top: -35px; /* 调整位置 */
  left: -35px; /* 调整位置 */
  box-shadow: 0px -4px 5px -5px rgba(0, 0, 0, 0.7);
}

/* 右下角的折角 */
body.bubblechat .mes:after {
  border-bottom: 30px solid color-mix(in srgb, var(--SmartThemeBodyColor), black 80%);
  opacity: 1 !important;
  bottom: -35px;
  right: -35px; /* 调整位置 */
  box-shadow: 0px 4px 5px -5px rgba(0, 0, 0, 0.7);
  }

  /* 头像 */
  .mesAvatarWrapper {
    display: flex;
    flex-direction: row; /* 从竖排改成横排 */
    padding-right: 10px;
    padding-left: 10px;
    margin-top: -270px;
    overflow: visible;
    width: 55%;
    height: 200px;
    outline: 10.6px solid var(--SmartThemeUserMesBlurTintColor);
    outline-offset: -10.6px;
    position: relative; /* 为伪元素定位 */
    z-index: 1;
}

/* 胶纸效果 */
.mesAvatarWrapper:after {
    content: " ";
    position: absolute;
    left: 65%;
    top: -15px;
    width: 120px;
    height: 30px;
    margin-left: -90px; /* 用于水平居中 */
    background: color-mix(in srgb, var(--SmartThemeBlurTintColor), transparent 50%);
    border: 1px solid color-mix(in srgb, var(--SmartThemeBlurTintColor), transparent 80%);
    box-shadow: 0px 0 3px rgba(0, 0, 0, 0.3);
    transform: rotate(-6deg);
    z-index: 10; /* 确保在最上层 */
}

  /* 头像处理 */
  .avatar img {
    border: none;
    box-shadow: none !important;
  }

  body.big-avatars .avatar {
    align-items: self-start !important;
  }

  /* 名字 */
.mes_block {
    padding-left: 0px !important;
    display: flex;
    flex-direction: column; /* 垂直排列 */
    align-items: center; /* 水平居中其子元素 */
    width: 100%; /* 占据父元素.mes提供的所有居中空间 */
    overflow: visible;
}

.mes_block .ch_name {
  background-color: var(--SmartThemeUserMesBlurTintColor);
  padding: 8px;
  padding-top: 0px !important;
  overflow: visible;
  width: 55%; /* 宽度填充父元素 */
  position: relative; /* 为伪元素定位 */
  margin-bottom: 30px;
}

/* 照片阴影 */
.mes_block .ch_name::before,
.mes_block .ch_name::after {
  content: "";
  position: absolute;
  z-index: -1; /* 确保阴影在元素下方 */
  bottom: 12px; /* 阴影与底部的距离 */
  width: 50%;   /* 阴影宽度 */
  height: 10px; /* 阴影高度 */
  box-shadow: 0 9px 14px black;
  transition: all 0.3s ease-in-out; /* 添加平滑过渡效果 */
}

/* 左侧阴影 */
.mes_block .ch_name::before {
  left: 15px;
  transform: skew(-5deg) rotate(-5deg);
}

/* 右侧阴影 */
.mes_block .ch_name::after {
  right: 15px;
  transform: skew(5deg) rotate(5deg);
}

/* 鼠标悬停时的阴影动效 */
.mes_block:hover .ch_name::before,
.mes_block:hover .ch_name::after {
    box-shadow: 0 2px 14px rgba(0, 0, 0, 0.4);
}

.mes_block:hover .ch_name::before {
    left: 5px;
}

.mes_block:hover .ch_name::after {
    right: 5px;
}

  /* 正文 */
.mes_text {
  padding: 0px 4px !important;
  font-size: var(--mainFontSize);
  line-height: calc(var(--mainFontSize) + .7rem);
}

  .mes_text p {
    margin-bottom: 12px;
    text-shadow: 0px 1px 0px var(--white20a) !important;
    font-size: var(--mainFontSize);
  }

  .mes_block {
    padding-left: 0px !important;
  }

  /* 正文edit按键 */
  .mes_edit_buttons {
    filter: drop-shadow(0px 0px 2px var(--buttonFill));
  }

  .mes_edit_buttons .menu_button, .mes_reasoning_actions .edit_button {
    color: var(--SmartThemeBodyColor);
  }

   /* 引用 */
  .mes_text blockquote, .mes_reasoning blockquote {
    padding: 8px;
    margin-bottom: 8px;
    text-decoration: line-through double var(--SmartThemeBorderColor) 5px;
  }

  /* 对白样式 */
  .mes_text q {
    color: var(--SmartThemeQuoteColor);
    margin: 0px 3px;
    font-size: var(--mainFontSize);
    text-decoration: line-through double var(--SmartThemeBorderColor) 5px;
  }

  /* extension */
  .extensions_info .extension_disabled {
    color: var(--SmartThemeEmColor);
  }

  /* 快捷回复 */
  .menu_button  {
    filter: none !important;
    background-color: var(--buttonFill) !important;
    border-radius: 4px !important;
    border:transparent !important;
  }

  .menu_button:hover {
    background-color: var(--crimson-hover) !important;
  }

  /* 输入助手 */
  .integrated-button-row {
    background-color: var(--menuBackgroundColor) !important;
  }

  /* 发送&top bar */
  #top-bar, #send_form {
    background-color: color-mix( in srgb, var(--navBackgroundColor) 50%, transparent 50%) !important;
    padding: 6px 8px;
    border: none;
  }

  body.no-blur #top-bar, body.no-blur #send_form {
    background-color: var(--navBackgroundColor) !important;
    padding: 6px 8px;
    border: none;
  }

  #send_form, body.no-blur #send_form {
    border-radius: var(--genericRadius) var(--genericRadius) 0px 0px;
  }

   /* 顶栏阴影 */
  body.no-blur #top-bar {
    box-shadow: none;
  }

   /* 发送栏阴影 */
   body.no-blur #send_form {
    box-shadow: var(--boxShadowSubtle);
  }

  .menu_button.menu_button_default {
    box-shadow: none !important;
  }

  /* Checkbox */
  input[type='checkbox'] {
    background-color: var(--buttonFill);
    border: transparent;
    box-shadow: none;
    border-radius: 2px;
  }

  input[type="checkbox"]::before {
    box-shadow: inset 1em 1em var(--SmartThemeEmColor);
  }

  input[type="checkbox"]:disabled {
    background-color: color-mix(in srgb, var(--buttonFill) 20%, black 10%);
    opacity: 0.7;
  }

  /* 预设 */
  #completion_prompt_manager_list {
    background-color: var(--menuBackgroundColor) !important;
    padding: 0px 8px;
  }

  #completion_prompt_manager .caution {
    color: var(--warning);
  }

  #completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt {
    background-color: var(--buttonFill);
    border: transparent;
    border-radius: 2px;
    padding: 8px;
    margin-bottom: 8px;
  }
  #completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt.completion_prompt_manager_prompt_disabled {
    background-color: var(--menuBackgroundColor) ;
    border: transparent;
    border-radius: 2px;
    padding: 8px;
    margin-bottom: 8px;
  }

  #completion_prompt_manager .completion_prompt_manager_header {
    color: var(--SmartThemeBodyColor);
  }

  .text_muted {
    color: var(--utilityColor);
  }

  /* 预设icon取消shadow */
  #completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt span span span,
  .mes_button, #completion_prompt_manager .completion_prompt_manager_header_advanced span{
    filter: none !important;
  }

  /* 预设inactive颜色 */
  #completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name, #completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name .prompt-manager-inspect-action {
    color: var(--utilityColor);
  }

/* 斜体文本 */
.mes_text em,
.mes_text i,
code:not(.mes_text pre code) {
    color: var(--SmartThemeEmColor);
    background-color: transparent !important;
    text-decoration: var(--SmartThemeEmColor) solid underline 1px;
    text-underline-offset: 3px;
    border: none !important;
    line-height: calc(var(--mainFontSize) + .7rem);
    text-shadow: 0px 1px 0px var(--white20a) !important;
    font-size: var(--mainFontSize);
}

.mes_text em,
.mes_text i {
    font-style: normal;
}

  /* Spacing Adjustment */
  .range-block-title {
    margin: 8px 0px;
    text-align: left;
  }

  hr {
    background-image: none;
    background-color: var(--SmartThemeUnderlineColor);
    margin: 12px 0px;
  }

  .range-block {
    margin-bottom: 8px;
  }
  .checkbox_label {
    margin: 2px 0px;
  }

  #result_info_text {
    color: var(--SmartThemeBodyColor);
  }

  .editable-slider-notification {
    top:10px;
  }

  /* 按键选项等颜色 */
  select, .range-block-counter input, .text_pole, textarea, .neo-range-input {
    filter: none !important;
    background-color: var(--buttonFill) !important;
    border: transparent !important;
    margin: 4px 0px !important;
  }

  /* 发送input */
  #send_textarea {
    background-color: transparent !important;
    border: none !important;
    font-size: calc(var(--mainFontSize) - 1px);
  }

  /* 圆形滑动条active */
  input.neo-range-slider[type="range"]::-webkit-slider-thumb {
    width: 12px;
    height: 12px;
    border: none;
    box-shadow: none !important;
    background-color: var(--scrollBarColor) !important;
  }

  input.neo-range-slider[type="range"] {
    box-shadow: none !important;
    background-color: var(--accentColor) !important;
    opacity: 1;
  }

  /* 圆形滑动条disabled */
  input.neo-range-slider[type="range"]:disabled::-webkit-slider-thumb {
    width: 12px;
    height: 12px;
    border: none;
    box-shadow: none !important;
